import React from 'react'
import Header from './layout.header'
import Footer from './layout.footer'

const layoutStyle = {
  margin: 20,
  padding: 20,
  border: '1px solid #DDD'
}

const Layout = props => (
  <div className={styles.layout}>
    <Header />
    {props.children}
    <Footer />
  </div>
)

export default Layout
